import i18n from 'i18next';
import { initReactI18next } from 'react-i18next';

// Impor file JSON untuk bahasa
const resources = {
  en: {
    translation: {
      "welcome": "Welcome to our website",
      "description": "This is a React multi-language example"
    }
  },
  id: {
    translation: {
      "welcome": "Selamat datang di website kami",
      "description": "Ini adalah contoh website multi-bahasa dengan React"
    }
  }
};

// Konfigurasi i18next
i18n
  .use(initReactI18next) // Inisialisasi react-i18next
  .init({
    resources,
    lng: 'id', // Bahasa default
    fallbackLng: 'en', // Jika bahasa tidak ditemukan, gunakan bahasa ini
    interpolation: {
      escapeValue: false, // React sudah aman dari XSS
    }
  });

export default i18n;
